memberdef.initializer:
do{				\
    if (mkdir(a,b) == -1) { 																\
        if (errno != EEXIST) {																					\
            char ErrorString[400];																			\
            sprintf(ErrorString,"FILE %s FUNC %s:%d Cannot create directory: %s",__FILE__,__FUNCTION__,__LINE__,a);	\
            FTI_Print(ErrorString, FTI_EROR); 																	\
            return FTI_NSCS; 																					\
        }																										\
    }																											\
}while(0)
